widgets.css:
.master-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  height: 100%;
}

.master-value {
  font-size: 100%;
  font-weight: 800;
  line-height: 200%;
  text-align: center;
  height: 20%;
}

.master-label {
  writing-mode: vertical-rl;
  text-orientation: upright;
  font-size: 16px;
  font-weight: 700;
  letter-spacing: 8px;
  opacity: 0.75;
  height: 80%;
}

